import React from 'react'
import { Container , Row, Col, Image, Button } from "react-bootstrap"


const GridSystem = (props) => {

    let indexFriends =0
    
    const buildGrid = ()=> {

        return (renderRows())
    
    }

    const renderRows = () =>{
        let rows = []

        for( let row = 0; row < props.rowcount ; row++ ){

            rows.push(
                <Row className="Row">
                    {
                        renderCols()
                    }
               </Row>
            )
        }

        return rows;

    }                               

    const renderCols = () =>{

        let cols = []

        for( let col=0; (col < props.colCount) && (indexFriends < props.ListFriends.length) ; col++ ){

            if(props.ListFriends!=null){
                cols.push(
                    <Col className="Col text-center">  
                        {
                            // <div className='wrapper'>
                            //       <Button onClick={e => props.cancelFriendshipClick(e.target.id)}  id={props.ListFriends[indexFriends].id} className='c1 mt-4'>cancel friendship</Button>
                            //       <div className='c3 d-inline-block'>{props.ListFriends[indexFriends].username}</div> 
                            //       <Image src={`data:image/jpeg;base64,${props.ListFriends[indexFriends++].image[0].content}`} className="grid-image mt-3 c2"  />
                            // </div> 
                            props.viewFriend
                        }
                    </Col>
                )
            }
        }

        return cols
}


return (

        <Container className="container w-50 p-3">
            {
            buildGrid()
            }
        </Container>

    );
};

export default GridSystem;
