import React, { Component } from 'react'

import {FloatingLabelsDate, FloatingLabelsEmail, FloatingLabelsPassword, FloatingLabelsPhone,
         FloatingLabelsUsername, SelectsGender, TextAreasDescription} from '@components/forms/index.js'

import {Container, Row, Col,Form   } from 'react-bootstrap'

import UserDataService from '@api/user/UserDataService.js'

class Test extends Component {

    constructor(props){
        super(props)

            this.state = {

                            id: '', 
                            username:   '',
                            gender: '', 
                            date:   '', 
                            phone:  '', 
                            email:  '', 
                            password:   '',
                            confirm: ''

            }
    }

    componentDidMount(){
        console.log("componentDidMount")
        this.refreshUsers();
    }
    
    refreshUsers(){
        UserDataService.retrieveUser(1)
        .then(response =>{
             this.setState({

                username:response.data.username,
                phone:  response.data.phone, 
                email:  response.data.email, 
            
             })
             }); 

             console.log("refreshUsers")
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
    })
    }

    handleSubmit = (event)=>{
        console.log(this.state.email)
        event.preventDefault();
    }


    render() {

        console.log("render")

        return (

           <>

                <Container>
                <Form onSubmit={this.handleSubmit} noValidate>
                   <Row className="justify-content-md-center">

                        <Col lg="4">
                            <FloatingLabelsPhone handleChange={this.handleChange} val={this.state.phone}/>
                        </Col>

                        <Col lg="4">
                            <FloatingLabelsEmail handleChange={this.handleChange} val={this.state.email}/>
                        </Col>

                   </Row>

                   <Row className="justify-content-md-center">

                        <Col    lg="8">

                            <FloatingLabelsUsername handleChange={this.handleChange} val={this.state.username}/>  

                        </Col>

                   </Row>

                    <Row className="justify-content-md-center">

                        <Col lg="4">
                            <FloatingLabelsPassword handleChange={this.handleChange} val={this.state.password} name="password"/>
                        </Col>

                        <Col lg="4">
                            <FloatingLabelsPassword handleChange={this.handleChange} val={this.state.confirm} name="confirm"/>
                        </Col>

                   </Row>

                    <Row className="justify-content-md-center">

                        <Col lg="4">
                            <FloatingLabelsDate handleChange={this.handleChange} val={this.state.date}/>
                        </Col>

                        <Col lg="4">
                            <SelectsGender/>
                        </Col>

                    </Row>

                    <Row className="justify-content-md-center">

                        <Col    lg="8">

                            < TextAreasDescription/>  

                        </Col>

                   </Row>

                   <button type="submit" value="submit" > submit </button>
                   </Form>
                 </Container> 
                 
           </>

        )
    }
}

export default Test
