class UserInfoUpdate extends Component {

    constructor(props){
        super(props)
            this.state = {
                title: "עדכון פרטים",
                user: 
                new User("","","","","","")
            }

            console.log("constructor")
            console.log(this.state.user)
    }

    componentDidMount(){
        this.refreshUsers();
        console.log("componentDidMount")
        console.log(this.state.user)
    }

