    //                                 response.data.phoneNumber,
    //                                 response.data.email)
    //            })
    //         }
    //     )
    // }

    
    refreshUsers(){
        UserDataService.retrieveUser(1)
        .then(response =>{
             this.setState(prevState => ({ user: prevState.user.copyConstructor(response.data)}))
             
             });

       
    }

    render() {

        console.log("render")
        console.log(this.state.user)

      return (

          <>    
             <UserInfoForm User={this.state.user} title={this.state.title}/>
          </>

      )

    }
}

export default UserInfoUpdate
