import React, {Component} from "react"
import AuthenticationService from "./AuthenticationService.js"
//import { isValidPhoneNumber } from "react-phone-number-input"
import {Card, Button, Alert } from "react-bootstrap"


class Login extends Component{

    constructor(props){
        super(props)
            this.state = {
                areacode: "+972",
                phonenumber: "",
                isValidPhoneNumber: true,
                password: "",
                isvalidPassword: true,
                stage: 1
            }
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
        })
    }

    continueClicked = () => {

        if(this.state.stage===1){

            if(isValidPhoneNumber(this.state.areacode + this.state.phonenumber)){

                this.setState(
                    {stage: 2}
                )

            }else{

                this.setState({
                    isValidPhoneNumber: false
                })

            }

        }

        if(this.state.stage===2){
            
            // if(this.state.password === "1234"){
            //     AuthenticationService.registerSuccessfulLogin(this.state.phonenumber,this.state.password);
            //     this.props.history.push('/Welcome')

            // }else{

            //     this.setState({  
            //     isvalidPassword: false

            //     }) 
            // }

        // AuthenticationService
        // .executeBasicAuthenticationService(this.state.phonenumber, this.state.password)
        // .then(() => {
        //     AuthenticationService.registerSuccessfulLogin(this.state.phonenumber,this.state.password)
        //     this.props.history.push("/welcome")
        // }).catch( () =>{
        //     this.setState({isvalidPassword: false})
        
        // })

        AuthenticationService
        .executeJwtAuthenticationService(this.state.phonenumber, this.state.password)
        .then((response) => {
            AuthenticationService.registerSuccessfulLoginForJwt(this.state.phonenumber, response.data.token)
            this.props.history.push("/welcome")
        }).catch(() => {
            this.setState({ isvalidPassword: false })
        })

        }

    }

    render(){
        return (

            <>
            
             { this.state.stage===1 &&  

                <div>
                    <Card className="mx-auto card-home-page">
                        <Card.Body>
                            <Card.Title>המספר שלי הוא</Card.Title>
                                {! this.state.isValidPhoneNumber && 
                                   <Alert variant="warning"> יש להזין מספר טלפון חוקי </Alert>}
                                   <input type="text" name="areacode" value={this.state.areacode} onChange={this.handleChange}></input>
                                   <input type="text" name="phonenumber" value={this.state.phonenumber} onChange={this.handleChange}></input>
                                   <button onClick={this.continueClicked}>המשך</button>
                            </Card.Body>
                    </Card>
                </div> } 

            {this.state.stage===2 && 

                <div>
                    <Card className="mx-auto card-home-page">
                        <Card.Body>
                            <Card.Title>הקוד שלי הוא</Card.Title>
                            {! this.state.isvalidPassword && 
                            <Alert variant="warning">הקוד שהזנת לא חוקי. יש לנסות שוב </Alert>}
                            <input type="password" name="password" onChange={this.handleChange}></input>
                            <button onClick={this.continueClicked}>המשך</button>
                        </Card.Body>
                    </Card>
                </div>}

            </>

        )
        
    }

}

export default Login;
