//import axios from "axios"

import Instance from "../../components/instance"

// const instance = getInstance(null,null);

class UserDataService{

    retrieveAllUsers(){
        
        return Instance.instance.get("http://localhost:8080/users")

    }

    retrieveUser(userId){

        return Instance.instance.delete(`http://localhost:8080/users/${userId}`)

    }

    deleteUser(userId){

        return Instance.instance.delete(`http://localhost:8080/users/${userId}`)

    }

    updateUser(user, userId){

        return Instance.instance.put(`http://localhost:8080/users/${userId}`, user)

    }

    createUser(user){

        return Instance.instance.post("http://localhost:8080/users", user)

    }

}

export default new UserDataService()
