import React, { Component } from 'react'
import {Image, Button} from "react-bootstrap"
import SubNavbar from "@components/SubNavbar.js";
import UserDataService from "@api/user/UserDataService";

class FindFriends extends Component {

  constructor(props){
    super(props)
        this.state = {
           search: '',
           searchResult: null,
           show: false,
           imageResult: '',
           idResult: '',
           usernameResult: ''
        }
  }

  handleChange = async (event) => {
    
    try{
    const searchResult = await UserDataService.retrieveUser(event.target.value)
 
      this.setState({
          [event.target.name] : event.target.value,
          searchResult : searchResult.data,
          show: true,
          imageResult: searchResult.data.image[0].content,
          idResult: searchResult.data.id,
          usernameResult: searchResult.data.username
      })
  
  }
  catch{
      this.setState({
        [event.target.name] : event.target.value,
        show: false
      })
  }

  }

  AddFriend = async (friendId) =>{

    var requestParam = {

      params: {
          userId: Number(this.state.id) ,
          otherId: friendId,
          type: 'FRIEND' ,
          status: 'ACTIVE' 
      }

  }
  
  await UserDataService.updateRelationship(this.state.id,requestParam);

  }

    render() {

        const title = "Find new friends"
        const btnText = "My friends"

        return (

            <div className="mx-auto">

                        <SubNavbar handleChange={this.handleChange} 
                                   valSerach={this.state.valSerach}
                                   btnText={btnText}
                                   title={title}/>

                        {this.state.show && <div className='w-50 mx-auto'>

                                                 <Image src={`data:image/jpeg;base64,${this.state.imageResult}`} 
                                                        className="grid-image mt-3 mb-3 d-block mx-auto" />  
   
                                                 <div className=' w-25 mx-auto'>{this.state.usernameResult}</div>
   
                                                 <div className='mx-auto'>

                                                       <Button onClick={e => this.AddFriend(e.target.id)}  
                                                               id={this.state.idResult} 
                                                               className='c1 mt-4  mx-auto'>Add Friend</Button>

                                                 </div>

                                            </div> }

            </div>

        )
    }
}

export default FindFriends
