import React, { Component } from 'react'
import UserDataService from "../api/user/UserDataService";
import {Button} from "react-bootstrap"

class Test extends Component {

    constructor(props){
        super(props)
            this.state = {
                message: "",
                inputValue: "",
                users: []
            }
    }


    componentDidMount(){
        this.refreshTodos();
    }

    refreshTodos(){
        UserDataService.retrieveAllUsers()
        .then(
            response=> {
           this.setState({   
                   users: response.data
               })
            }
        )
    }

    deleteUserClicked = (userId) =>{
        console.log(userId)
        UserDataService.deleteUser(userId)
            .then(
                response =>{
                    this.setState({
                        message: `Delete of user ${userId} successful `
                    })
                    this.refreshTodos();
                }
            )
    }

    deleteTodoClicked(id) {
        let username = AuthenticationService.getLoggedInUserName()
        //console.log(id + " " + username);
        TodoDataService.deleteTodo(username, id)
            .then(
                response => {
                    this.setState({ message: `Delete of todo ${id} Successful` })
                    this.refreshTodos()
                }
            )

    }

    updateUserClicked = (userId) =>{
        UserDataService.updateUser({id: 3, username:"yaniv"},userId)
            .then(
                this.refreshTodos()
            );
        console.log(userId)
    }

    createUserClicked = () =>{
        UserDataService.createUser({id: 0,username: "yoni"})
            .then(
                this.refreshTodos()
            );
    }

    updateInputValue = (props) =>{
        this.setState({
            inputValue : props.target.value
        });
    }

    render() {
        return (

            <>
            <input type="text" value={this.state.inputValue} onChange={this.updateInputValue} />
            <Button onClick={ () => this.deleteUserClicked(this.state.inputValue) }>מחק</Button>
            <Button onClick={ () => this.updateUserClicked(this.state.inputValue) }>עדכן</Button>
            <Button onClick={ () => this.createUserClicked() }>הוסף</Button>
            <div className="btn btn-warning" >{this.state.message}</div>

            <ul>
                {this.state.users.map(u=><li>{u.id}</li>)}
            </ul>

            </>

        )
    }
}

export default Test
