import React, {Component} from "react";
import UserDataService from "../../api/user/UserDataService";
import AuthenticationService from "@services/AuthenticationService.js"
import GridSystem from './component/GridSystem.js';
import viewFriends from './component/viewFriends.js'

class ShowFriends extends Component {
  
  constructor(props){
    super(props)
        this.state = {
            id: AuthenticationService.getLoggedInUserId(),
            ListFriends: null
        }
    }

  componentDidMount(){
    this.refresh()
  }

  cancelFriendshipClick = async (friendId) =>{

    var requestParam = {
        params: {
          userId: Number(this.state.id) ,
          friendId: Number(friendId), 
      }
    }

    await UserDataService.deleteRelationship(this.state.id,requestParam);

    this.refresh()

  }

  refresh = async () =>{
    const res =  await UserDataService.getFriends(this.state.id);
    this.setState({ListFriends : res.data})
  }

  getNumRow = (colCount) =>{

    let rowcount

    if(this.state.ListFriends!=null){
      rowcount = Math.ceil(this.state.ListFriends.length / colCount) ;
     }else{
       rowcount = 0;
     }

     return rowcount
  }


  render() {

    const colCount = 2
    const rowcount = this.getNumRow(colCount)

    return (

      <div className="mx-auto">
               <GridSystem rowcount = {rowcount} colCount = {colCount}
                           ListFriends={this.state.ListFriends} 
                           cancelFriendshipClick = {this.cancelFriendshipClick} 
               />
      </div>

    );
  }
}

export default ShowFriends
