import React, { Component } from "react";
import { Stage, Layer, Circle, Line } from "react-konva";

class Index extends Component {
  state = {
    window: {},
    nodes: [
      {
        id: 1,
        name: "A",
        position: {
          x: 100,
          y: 100
        }
      },
      {
        id: 2,
        name: "B",
        position: {
          x: 400,
          y: 100
        }
      }
    ],
    link: {
      source: "1",
      target: "2"
    }
  };

  componentDidMount() {
    this.setState({ window });
  }

  handleDrag = node => e => {
    const nodes = [...this.state.nodes];
    const newObj = Object.assign({}, node, {
      position: { x: e.target.x(), y: e.target.y() }
    });
    const index = nodes.indexOf(node);
    nodes[index] = newObj;
    this.setState({ nodes });
  };

  render() {
    const { nodes, window } = this.state;
    return (
      <Stage width={window.innerWidth} height={window.innerHeight}>
        <Layer>
          <Line
            points={[
              nodes[0].position.x,
              nodes[0].position.y,
              nodes[1].position.x,
              nodes[1].position.y
            ]}
            stroke="black"
            fill="black"
            strokeWidth={4}
          />
          {nodes.map(node => (
            <Circle
              x={node.position.x}
              y={node.position.y}
              radius={50}
              fill="green"
              key={node.id}
              draggable
              onDragMove={this.handleDrag(node)}
            />
          ))}
        </Layer>
      </Stage>
    );
  }
}

export default Index;

