import React, { Component } from 'react'

import {FloatingLabelsDate, FloatingLabelsEmail, FloatingLabelsPassword, FloatingLabelsPhone,
         FloatingLabelsUsername, SelectsGender, TextAreasDescription} from '@components/forms/index.js'

import DateHelpers from '@utils/helpers/DateHelpers.js'

import {Container, Row, Col,Form, Button} from 'react-bootstrap'

import UserDataService from '@api/user/UserDataService.js'

import AuthenticationService from '@services/AuthenticationService.js'

import {User} from '@components/User.js'

import {withRouter} from 'react-router-dom';

class PersonalDetailsUpdateForm extends Component {

    constructor(props){
        super(props)

            this.state = {

                            id: 4, 
                            username:   '',
                            gender: '', 
                            date:   DateHelpers.getTodayEighteenYearsAgo(),
                            phone:  '', 
                            email:  '', 
                            password:   '',
                            confirm: '',
                            description: '',
                            validated: false
                          
            }
    }

    componentDidMount(){
        this.refresh()
    }

    async refresh(){
        await UserDataService.retrieveUser(this.state.id)
        .then( (response) =>{
            this.setState({
                ...response.data
            })
        } )
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
    })
    }

    handleSubmit = (event)=>{

        if(this.isValid(event)){
            this.updateAccount()
            event.preventDefault();
        }

        event.preventDefault();

    }

    updateAccount = async () =>{
    
        let token
        let user = new User()
        user.copy(this.state)
        
        await AuthenticationService.executeJwtAuthenticationService(this.state.username,this.state.password)
        .then( (response) => {    
           AuthenticationService.registerSuccessfulLoginForJwt(this.state.username)
           token = response.data.token;
        })

        AuthenticationService.setInstance(token)

        await UserDataService.updateUser(user,this.state.id)
        .then(() => this.props.history.push('/search/match/'))

    }

    isValid = (event) =>{
        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            this.setState({validated: true})
             return false
        }
        
        return true
    }

    render() {

        const btnTxt = "Update"
       
        return (

           <>

                <Container className='mt-4'>
                <Form onSubmit={this.handleSubmit} noValidate validated={this.state.validated}>
                   <Row className="justify-content-md-center">

                        <Col lg="4">
                            <FloatingLabelsPhone handleChange={this.handleChange} val={this.state.phone}/>
                        </Col>

                        <Col lg="4">
                            <FloatingLabelsEmail handleChange={this.handleChange} val={this.state.email}/>
                        </Col>

                   </Row>

                   <Row className="justify-content-md-center">

                        <Col    lg="8">

                            <FloatingLabelsUsername handleChange={this.handleChange} val={this.state.username}/>  

                        </Col>

                   </Row>

                    <Row className="justify-content-md-center">

                        <Col lg="4">
                            <FloatingLabelsPassword handleChange={this.handleChange} val={this.state.password} name="password"/>
                        </Col>

                        <Col lg="4">
                            <FloatingLabelsPassword handleChange={this.handleChange} val={this.state.confirm} name="confirm"/>
                        </Col>

                   </Row>

                    <Row className="justify-content-md-center">

                        <Col lg="4">
                            <FloatingLabelsDate handleChange={this.handleChange} val={this.state.date}/>
                        </Col>

                        <Col lg="4">
                            <SelectsGender handleChange={this.handleChange} val={this.state.gender}/>
                        </Col>

                    </Row>

                    <Row className="justify-content-md-center">

                        <Col    lg="8">

                            < TextAreasDescription handleChange={this.handleChange} val={this.state.description}/>  

                        </Col>

                   </Row>

                   <Button as="input" type="submit" value={btnTxt} className='mt-3 mb-5'  />

                   </Form>
                 </Container> 
                 
           </>

        )
    }
}

export default withRouter(PersonalDetailsUpdateForm)
