import axios from "axios"
import Login from "../../components/Login"

const func = ()=>{

    consoleLogin.getVerificationInformation();

    let username = 'in28minutes'
    let password = 'dummy'

    let basicAuthHeaderString = 'Basic ' + window.btoa(`${username}:${password}`);

    return { headers: { Authorization: basicAuthHeaderString } }
}

class UserDataService{

    retrieveAllUsers(){

        return axios.get("http://localhost:8080/users",func())

    }

    retrieveUser(userId){

        return axios.delete(`http://localhost:8080/users/${userId}`,func())

    }

    deleteUser(userId){

        return axios.delete(`http://localhost:8080/users/${userId}`,func())

    }

    updateUser(user, userId){

        return axios.put(`http://localhost:8080/users/${userId}`, user,func())

    }

    createUser(user){

        return axios.post("http://localhost:8080/users", user,func())

    }

}

export default new UserDataService()
