import Xarrow from "react-xarrows";
import GridSystem from './GridSystem.js';

class VisualizationSocialConnections extends Component {


    constructor(props){
        super(props)
            this.state = {
                conection: 
                        [  {flag : false, id: "1", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
