                         ],
                currentId : 0        
            }
    }

    render() {
        return (

            <>

                <GridSystem colCount={5} md={6}>
                {
                this.state.conection.map(node => { if(node.flag) {return <div style={{marginTop: "40px"}}> 
                                                        <img src={node.image} class="rounded-circle" id={node.id} style={{width: "100px", height: "100px"}} ></img>
                                                   </div>}
                                                   else {return <div> </div>}}) 
                }
                </GridSystem>

                {
                    this.state.conection.map(node => {
                        if(node.flag && node.next!=null){
                            return (<Xarrow
                                        start={node.id} //can be react ref
                                        end= {node.next} react ref
                                    />)
                        }
                    })
                }

            </>
        )
    }
}

