import React, { Component } from 'react'
import UserInfoForm from './UserInfoForm'
import moment from 'moment'
import {User} from "./User"
import UserDataService from "../api/user/UserDataService";

class UserInfoUpdate extends Component {

    constructor(props){
        super(props)
            this.state = {
                title: "עדכון פרטים",
                users: new User("","","",date,empty,empty)
            }
    }

    componentDidMount(){
        this.refreshUsers();
    }

    refreshUsers(){
        UserDataService.retrieveAllUsers()
        .then(
            response=> {
           this.setState({   
                   users: response.data
               })
               console.log("retrieveAllUsers");
            }
        )
    }

    render() {

      let title = "עדכון פרטים";

      let empty = ""

      let user;

      UserDataService.retrieveUser(1)
        .then(response=>{
            user = new User(response.data.id,
                            response.data.username,
                            response.data.gender,
                            response.data.date,
                            response.data.phoneNumber,
                            response.data.email)
        })
      

      return (

          <>
       
            <UserInfoForm User={user} title={title}/>
          </>

      )

    }
}

export default UserInfoUpdate
