import React, {Component} from "react"
import AuthenticationService from "@services/AuthenticationService.js"
import { FloatingLabelsPassword, FloatingLabelsPhone, FloatingLabelsEmail } from '@components/forms/floating_labels/index.js'
import {Container, Row, Col, Button, Form} from 'react-bootstrap'


class Login extends Component{

        constructor(props){
            super(props)
                this.state = {
                    areacode: '+972',
                    phone: '',
                    email: '',
                    password: '',
                    validated: false
                }
        }

        handleChange = (event) => {
            this.setState({
                [event.target.name] : event.target.value
            })
        }

        handleSubmit = (event) => {

            if(this.isValid(event)){
                //if(AuthenticationService.executeAuthenticationService(this.state.phonenumber, this.state.password)){
                    AuthenticationService.registerSuccessfulLogin(this.state.phonenumber)
                    this.props.history.push('/search/match/')
                //}
            }
            
            event.preventDefault();

        }

        isValid = (event) =>{
            const form = event.currentTarget;
            if (form.checkValidity() === false) {
                this.setState({validated: true})
                 return false
            }
            
            return true
        }


        render(){
            return (

                <>

                    <Container className='mt-5'>

                        <Form onSubmit={this.handleSubmit} noValidate validated={this.state.validated}>

                            <Row className="justify-content-md-center">

                                <Col lg="4">
                                    <p class="fs-3">Enter your number phone:</p>
                                </Col>

                            </Row>

                            <Row className="justify-content-md-center">

                                <Col lg="4">
                                    <FloatingLabelsPhone handleChange={this.handleChange} val={this.state.phone} name="phone"/>
                                </Col>

                            </Row>

                            <Row className="justify-content-md-center">

                                <Col lg="4">
                                    <p class="fs-3"> Or</p>
                                </Col>

                            </Row>

                            <Row className="justify-content-md-center">

                                <Col lg="4">
                                    <p class="fs-3"> Enter your email:</p>
                                </Col>

                            </Row>

                            <Row className="justify-content-md-center">

                                <Col lg="4">
                                    <FloatingLabelsEmail handleChange={this.handleChange} val={this.state.email} name="email"/>
                                </Col>

                            </Row>

                            <Row className="justify-content-md-center mt-3">

                                <Col lg="4">
                                    <p class="fs-3">Enter your password:</p>
                                </Col>

                            </Row>

                            <Row className="justify-content-md-center">

                                <Col lg="4">
                                    <FloatingLabelsPassword handleChange={this.handleChange} val={this.state.password} name="password"/>
                                </Col>

                            </Row>

                            <Row className="justify-content-md-center">

                                <Col lg="4">
                                    < Button size="lg" className='mb-2 mt-3 w-50' type="submit" >Login</Button>
                                </Col>

                            </Row>

                        </Form>

                    </Container>

                </>

            )
            
        }

}

export default Login;
