import React, {Component} from "react";
import UserDataService from "@api/user/UserDataService";
import GridSystem from '@components/GridSystem.js';

class ShowFriendRequests extends Component {
  
  constructor(props){
    super(props)
        this.state = {

            listFriendRequests: null

        }
  }

  componentDidMount(){
    this.refresh()
  }

  refresh = async () =>{

    var requestParam = {
        params: {
          type: "F",
          status: "S"
        }
    }

    const listFriendRequests = await UserDataService.getRelationships(this.state.id,requestParam);

    this.setState({listFriendRequests: listFriendRequests.data})

  }

  render() {

    const colCount = 2
    const rowcount = this.getNumRow(colCount)
    const ListViewFriends = this.buildListViewFriends()

    return (

      <div className="mx-auto">

                {/* <SubNavbar handleChange={this.handleChange} valSerach={this.state.valSerach}/> */}

                <GridSystem rowcount = {rowcount} 
                            colCount = {colCount} 
                           view={ListViewFriends}
                />
            
      </div>

    );
  }
}

export default ShowFriendRequests
