import React, {Component} from "react";
import InfiniteScroll from "react-infinite-scroll-component";
import {Image,Button,Container,Row,Col} from "react-bootstrap";
import UserDataService from "../api/user/UserDataService";
import AuthenticationService from "./AuthenticationService.js"

class ShowFriends extends Component {
  
  constructor(props){
    super(props)
        this.state = {
            friends: [],
            hasMore: true,
            valSearch: "",
            count: 0
        }
    }

    handleChange = (event) => {
    this.setState({
        [event.target.name] : event.target.value
    })
    }

  getFriends = () =>{
    return this.state.friends.map((f) => (
      <div className="bdika">
            <Button className="show-friens-btn">שלח הודעה</Button>
            <span className="">{f.name}</span>
            <Image src="https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg"  className="show-friens-image"/>
      </div>
    ))
  }

  fetchMoreData= () =>{

  }

  componentDidMount(){
    let username = AuthenticationService.getLoggedInUserName()
    UserDataService.retrieveAllfriends()
      .then(response => {
        this.setState({
          friends: response.data 
        })
      })

  }


  render() {
    return (
      <div>

         <Container className="show-friens-container">
                <Row>
                    <Col className="show-friens-col-2">
                          <input className="show-friens-col-1" type="text" name="valSearch" value={this.state.valSearch} placeholder="חיפוש" onChange={this.handleChange}></input>
                    </Col>
                    <Col className="show-friens-col-1">חברים</Col>
                </Row>
          </Container>
        
        <InfiniteScroll
          dataLength={this.state.friends.length}
          next={true}
          hasMore={this.state.hasMore}
          loader={<h4>Loading...</h4>}
          height={500}
          endMessage={null}
        >

        <div className="show-friens-div-image">
            {this.getFriends()}
        </div>

        </InfiniteScroll>
      </div>
    );
  }
}

export default ShowFriends

