import React, { Component } from 'react'
import ViewerNetworkVisualization from "./components/Viewer_network_visualization/ViewerNetworkVisualization.js"
import ViewerPotenMatch from "./components/viewer_potenMatch/ViewerPotenMatch.js"
import {UserDataService} from '@api/index.js'
import AuthenticationService from '@services/AuthenticationService.js'

class SearchMatch extends Component {
 
    constructor(props){
         super(props)
           this.state = {
                   id: AuthenticationService.getLoggedInUserId(),
                   optionMatches: null,
                   indexCurrentMatch: 0,
                   pathes: null,
                   //DataForViewerNetworkVisualization: null,
                   initialIndexPathSearch: 0 
            }
    }

    componentDidMount(){
         this.refresh()
    }

    refresh = async () =>{
    
        const resOptionMatches = await UserDataService.getOptionMatches(this.state.id); 
        const resPathes = await UserDataService.getPathes(this.state.id, resOptionMatches.data[0].id ); 
        const resMyImages = await UserDataService.getImages(this.state.id);
        //const DataForViewerNetworkVisualization =  this.BuildDataForViewerNetworkVisualization( resPathes.data, resOptionMatches.data[0], resMyImages.data, this.state.initialIndexPathSearch );

        this.setState(
            { 
                optionMatches: resOptionMatches.data,
                pathes: resPathes.data,
                //DataForViewerNetworkVisualization: DataForViewerNetworkVisualization,
                myImages: resMyImages.data 
            }
        )
    }

    likeClicked = async () =>{
        
        if(this.state.indexCurrentMatch < this.state.optionMatches.length -1 )
            this.setState(prevstate => (
                { 
                  indexCurrentMatch: prevstate.indexCurrentMatch + 1,
                  pathes: this.updatePathes(prevstate.indexCurrentMatch + 1) 
                }
            ))
        else{
            this.setState(
                { 
                  indexCurrentMatch: 0,
                  pathes: this.updatePathes(0) 
                }
            )
        }    
    }

    backClicked = () =>{

        if(this.state.indexCurrentMatch == 0 )
            this.setState(prevstate => (
                { indexCurrentMatch: this.state.optionMatches.length -1}
            ))
        else{
            this.setState(prevstate => (
                { indexCurrentMatch: prevstate.indexCurrentMatch - 1}
            
            ))
        }
    }

    unlikeClicked = () =>{

        if(this.state.indexCurrentMatch < this.state.optionMatches.length -1 )
            this.setState(prevstate => (
                { indexCurrentMatch: prevstate.indexCurrentMatch + 1}
            ))
        else{
            this.setState(
                { indexCurrentMatch: 0}
            )
        }  
    }  
     
    getMorePathes = () =>{

        this.setState(prevstate => (
            { 
                initialIndexPathSearch: prevstate.initialIndexPathSearch + 4,
                DataForViewerNetworkVisualization: this.BuildDataForViewerNetworkVisualization( this.state.pathes,this.state.optionMatches[this.state.indexCurrentMatch], this.state.myImages,  prevstate.initialIndexPathSearch + 4)
            }
        ))

    }

    updatePathes = async (indexCurrentMatch) =>{
        const optionMatchId = this.state.optionMatches[indexCurrentMatch].id;
        const res = await UserDataService.getPathes(this.state.id,optionMatchId)
        return res.data
    }

    BuildDataForViewerNetworkVisualization = (pathes, optionMatch, myImages, startIndex) =>{

        if( pathes!= null && optionMatch!=null){
            
            const gridSystemPathesIndex = [ ["12","6","2","1"] , ["13","8","9","3"] , ["18","23","24","29"] , ["17","22","21","26"] ]
             
            const gridSystemPathesData = Array.apply(null, Array(30)).map(function () {})
        
            let z = 0
    
            if(startIndex >= pathes.length){
                this.setState({initialIndexPathSearch: 0})
                startIndex = 0;
            }

            for( let i = startIndex, w=0 ; i < pathes.length && i <= (startIndex+ 3) ; i++, w++ ){
                for(let j = 0 ;j <= pathes[i].length; j++){
                
                    if(j==0){
                        gridSystemPathesData[z++] = { next: gridSystemPathesIndex[w][j+1]  ,  index: gridSystemPathesIndex[w][j],  content: myImages[0].content}
                    }
                    else{
                        if( j == (pathes[i].length) ){
                            gridSystemPathesData[z++] = {next: gridSystemPathesIndex[w][j+1]  ,  index: gridSystemPathesIndex[w][j],  content: pathes[i][j-1].image[0].content}
                            gridSystemPathesData[z++] = {next: null  ,  index: gridSystemPathesIndex[w][j+1],  content: optionMatch.image[0].content}
                        }
                        else{
                            gridSystemPathesData[z++] = {next: gridSystemPathesIndex[w][j+1]  ,  index: gridSystemPathesIndex[w][j],  content: pathes[i][j-1].image[0].content }  
                        }

                    }
                }
            }
            
            return gridSystemPathesData.sort(this.compare);
        }
        else{
            return null
        }       
    }

    compare = ( a, b ) => {
        if ( Number(a.index) < Number(b.index) ){
          return -1;
        }
        if ( Number(a.index) > Number(b.index) ){
          return 1;
        }
        return 0;
    }

    render() {

        let optionMatch;

        if( this.state.optionMatches != null )
             optionMatch = this.state.optionMatches[this.state.indexCurrentMatch];
        else
             optionMatch = null;

        return (
            <div>
               
               <ViewerNetworkVisualization data={this.state.DataForViewerNetworkVisualization} /> 
                            
               <ViewerPotenMatch likeClicked = {this.likeClicked} 
                                 backClicked = {this.backClicked}
                                 unlikeClicked = {this.unlikeClicked}
                                 getMorePathes = {this.getMorePathes}
                                 optionMatch = {optionMatch} /> 
        
            </div>
        )
    }
}

export default SearchMatch
