//import axios from "axios"

import getInstance from "../../components/instance"

import AuthenticationService from "../../components/AuthenticationService.js"

const instance = getInstance(AuthenticationService.x,AuthenticationService.y);

class UserDataService{

    retrieveAllUsers(){
        
        return instance.get("http://localhost:8080/users")

    }

    retrieveUser(userId){

        return instance.delete(`http://localhost:8080/users/${userId}`)

    }

    deleteUser(userId){

        return instance.delete(`http://localhost:8080/users/${userId}`)

    }

    updateUser(user, userId){

        return instance.put(`http://localhost:8080/users/${userId}`, user)

    }

    createUser(user){

        return instance.post("http://localhost:8080/users", user)

    }

}

export default new UserDataService()
