import React, { Component } from 'react'
import UserDataService from '@api/user/UserDataService.js'
import DateHelpers from '@utils/helpers/DateHelpers.js'

class FileUpload extends Component {

  constructor(props) {

    super(props);

    this.state ={
      file:  null,
      image: null,
      flag:  false,
    }
    
  }
  
  componentDidMount(){
    this.refresh()
  }

  refresh = async ()=>{
 
     const res = await UserDataService.getImages(1);

     this.setState({
               image: res.data[0].content,
               flag: true
      })
  
  }

  
  onChange = (e) => {
    console.log(e.target.files[0])
    this.setState({file:e.target.files[0]})
  }
  
  fileUpload =  async(file) => {
    const formData = new FormData();
    formData.append('image',file)
    await UserDataService.uploadImage(1,formData)
  }
  
  render() {

    let image = this.state.image

    const ViewImage = ({ image }) => <img src={`data:image/jpeg;base64,${image}`} />

    return (

      <>

             {this.state.flag && <ViewImage image = {image} />}
             <input type="file" name="image_1" onChange={props.imageHandleChange}/>
             <input type="file" name="image_2" onChange={props.imageHandleChange}/>

      </>
    )

  }
}

export default FileUpload
