import React, { Component } from 'react'
import UserInfoForm from './UserInfoForm'
import moment from 'moment'
import {User} from "./User"
import UserDataService from "../api/user/UserDataService";


class UserInfoUpdate extends Component {

    constructor(props){
        super(props)
            this.state = {
                title: "עדכון פרטים",
                user: new User("","","","","","")
            }
    }

    componentDidMount(){
        this.refreshUsers();
    }

    refreshUsers(){
        UserDataService.retrieveUser(1)
        .then(
            response=> {
           this.setState({   
                   user:  new User(response.data.id,
                                    response.data.username,
                                    response.data.gender,
                                    moment(response.data.date).format('YYYY-MM-DD'),
                                    response.data.phoneNumber,
                                    response.data.email)
               })
            }
        )
    }

    
    // refreshUsers(){
    //     UserDataService.retrieveUser(1)
    //     .then((response) => JSON.parse(response)) 
    //     .then((response) => this.setState({ user: response.data }));
    // }

    render() {

        console.log("render")
        console.log(this.state.user)

      return (

          <>    
             <UserInfoForm User={this.state.user} title={this.state.title}/>
          </>

      )

    }
}

export default UserInfoUpdate
