import React, { Component } from 'react'
import ViewerNetworkVisualization from "./components/Viewer_network_visualization/ViewerNetworkVisualization.js"
import ViewerPotenMatch from "./components/viewer_potenMatch/ViewerPotenMatch.js"
import {UserDataService} from '@api/index.js'
import AuthenticationService from '@services/AuthenticationService.js'

class SearchMatch extends Component {
 
    constructor(props){
         super(props)
           this.state = {
                   id: AuthenticationService.getLoggedInUserId(),
                   optionMatches: null,
                   indexCurrentMatch: 0,
                   pathes: null,
                   myImages: null,
                   initialIndexPathSearch: 0,
                   matches: null 
            }
    }

    componentDidMount(){
         this.refresh()
    }

    refresh = async () =>{
    
        const resOptionMatches = await UserDataService.getOptionMatches(this.state.id); 
        const resPathes = await UserDataService.getPathes(this.state.id, resOptionMatches.data[0].id ); 
        const resMyImages = await UserDataService.getImages(this.state.id);
        const matches = await UserDataService.getMatches(this.state.id);

        this.setState(
            { 
                optionMatches: resOptionMatches.data,
                pathes: resPathes.data,
                myImages: resMyImages.data, 
                matches: matches
            }
        )
    }

    likeClicked = async () =>{
        
        let resPathes = null
        let newIndexCurrentMatch = 0

        const isNewMatch = this.isNewMatch(this.state.matches, this.state.optionMatches[this.state.indexCurrentMatch].id)
        const TargetAndSource = new Map();

        if(isNewMatch){

            const iAmTargetOrSource = iAmTargetOrSource(this.state.matches, this.state.optionMatches[this.state.indexCurrentMatch].id)

            if( iAmTargetOrSource === 'T' ){
                TargetAndSource.set("targetId", this.state.id)
                TargetAndSource.set("sourceId", this.state.optionMatches[this.state.indexCurrentMatch].id)
                UserDataService.updateMatch(this.state.id,TargetAndSource);
            }
            else{
                TargetAndSource.set("targetId", this.state.optionMatches[this.state.indexCurrentMatch].id)
                TargetAndSource.set("sourceId", Number(this.state.id))
                UserDataService.updateMatch(this.state.id,TargetAndSource);
            }    

        }else{
            TargetAndSource.set("targetId", this.state.id)
            TargetAndSource.set("sourceId", this.state.optionMatches[this.state.indexCurrentMatch].id)
            UserDataService.createMatch(this.state.id,TargetAndSource);
        }

        const obj = Object.fromEntries(map1);

        if(this.state.indexCurrentMatch < this.state.optionMatches.length -1 ){
            resPathes = await UserDataService.getPathes(this.state.id, this.state.optionMatches[this.state.indexCurrentMatch+1].id )
            newIndexCurrentMatch = this.state.indexCurrentMatch + 1 
        }else{
            resPathes = await UserDataService.getPathes(this.state.id, this.state.optionMatches[0].id )
        }
        
        this.setState(
                { 
                  indexCurrentMatch: newIndexCurrentMatch,
                  pathes: resPathes.data 
                }
        )
    }

    backClicked = () =>{

        if(this.state.indexCurrentMatch == 0 )
            this.setState(prevstate => (
                { indexCurrentMatch: this.state.optionMatches.length -1}
            ))
        else{
            this.setState(prevstate => (
                { indexCurrentMatch: prevstate.indexCurrentMatch - 1}
            
            ))
        }
    }

    unlikeClicked = async () =>{

        let resPathes = null
        let newIndexCurrentMatch = 0

        if(this.state.indexCurrentMatch < this.state.optionMatches.length -1 ){
            resPathes = await UserDataService.getPathes(this.state.id, this.state.optionMatches[this.state.indexCurrentMatch+1].id )
            newIndexCurrentMatch = this.state.indexCurrentMatch + 1 
        }else{
            resPathes = await UserDataService.getPathes(this.state.id, this.state.optionMatches[0].id )
        }
        
        this.setState(
                { 
                  indexCurrentMatch: newIndexCurrentMatch,
                  pathes: resPathes.data 
                }
        ) 

    }  
     
    getMorePathes = () =>{

        this.setState(prevstate => (
            { 
                initialIndexPathSearch: prevstate.initialIndexPathSearch + 4,
            }
        ))

    }

    updateInitialIndexPathSearchToZero = () =>{
                this.setState({initialIndexPathSearch: 0})
    }

    isNewMatch = (matches, optionMatchId) =>{

        for( let i=0; i< matches.length; i++ ){
            if( matches[i].source == optionMatchId || matches[i].target == optionMatchId)
                return true
        }

        return false

    }

    iAmTargetOrSource = (matches, optionMatchId) =>{

        for( let i=0; i< matches.length; i++ ){

            if( matches[i].source == optionMatchId){
                return 'T';
            }

            if(matches[i].target == optionMatchId){
                return 'S';
            }
    }

        return false

    }



    render() {

        let optionMatch;

        if( this.state.optionMatches != null )
             optionMatch = this.state.optionMatches[this.state.indexCurrentMatch];
        else
             optionMatch = null;

        return (
            <div>
               
               <ViewerNetworkVisualization pathes={this.state.pathes} 
                                           optionMatch={optionMatch}
                                           myImages={this.state.myImages} 
                                           startIndex={this.state.initialIndexPathSearch}
                                           updateInitialIndexPathSearchToZero = {this.updateInitialIndexPathSearchToZero} /> 

               <ViewerPotenMatch likeClicked = {this.likeClicked} 
                                 backClicked = {this.backClicked}
                                 unlikeClicked = {this.unlikeClicked}
                                 getMorePathes = {this.getMorePathes}
                                 optionMatch = {optionMatch} /> 
        
            </div>
        )
    }
}

export default SearchMatch
