import {getAxiosInstance} from "../xhr/Axios"
import {JPA_API_URL} from "@utils/url/Constants.js"



const instance = getAxiosInstance(); 

class UserDataService{

    retrieveAllUsers(){
     
        return instance.get(`${JPA_API_URL}/users`)

    }

    retrieveUser(id){

        return instance.get(`${JPA_API_URL}/users/${id}`)

    }

    deleteUser(id) {
       
        return instance.delete(`${JPA_API_URL}/users/${id}`)
    }

    updateUser(user, id){

        return instance.put(`${JPA_API_URL}/users/${id}`, user)

    }

    async createUser(user){

        return instance.post(`${JPA_API_URL}/users/`, user)

    }

}

export default new UserDataService()
