import React, { Component } from 'react'
import AuthenticationService from '@services/AuthenticationService.js'
import UserDataService from '@api/user/UserDataService.js'

class ReactFileUpload extends Component {

  constructor(props) {
    super(props);
    this.state ={
      file:null,
      image: null

    }
    this.onFormSubmit = this.onFormSubmit.bind(this)
    this.onChange = this.onChange.bind(this)
    this.fileUpload = this.fileUpload.bind(this)
    this.getImage  = this.getImage.bind(this)
  }

  componentDidMount(){
    
  }

  onFormSubmit(e){
    e.preventDefault() // Stop form submit
    this.fileUpload(this.state.file); 
  }

  onChange(e) {
    this.setState({file:e.target.files[0]})
  }

  async fileUpload(file){
    const formData = new FormData();
    formData.append('image',file)
    await UserDataService.uploadImage(1,formData)
  }

   async getImage(){
    console.log("getImage")
    const res = await UserDataService.getImage(1)
    this.setState({image: res.data.content});       
  }

  render() {

    if(this.state.image==null)
      this.getImage()

    const data = this.state.image

    console.log("render data: " + data)

    const Example = ({ data }) => <img src={`data:image/jpeg;base64,${data}`} />

    return (

      <>
             <Example data={data} />
        
              <form onSubmit={this.onFormSubmit}>
                <h1>File Upload</h1>
                <input type="file" onChange={this.onChange} />
                <button as="input" type="submit">Upload</button>
              </form>
        
      </>
   )
  }
}

export default ReactFileUpload


