        UserDataService.createUser(user)
           .then(() => this.props.history.push('/registration'))
            

    }

    render() {

    const title = "עדכון פרטים"

      return (

          <>    
             <UserInfoForm user={this.state.user} title={title} submit={this.update}/>
          </>

      )

    }
}

