import React, { Component } from 'react'
import UserInfoForm from './UserInfoForm'
import moment from 'moment'
import {User} from "./User"
import UserDataService from "../api/user/UserDataService";


class UserInfoUpdate extends Component {

    constructor(props){
        super(props)
            this.state = {
                title: "עדכון פרטים",
                user: new User("","","","","","")
            }

            console.log(" constructor")
            console.log(this.state.user)
    }

    componentDidMount(){
        this.refreshUsers();
        console.log("componentDidMount")
        console.log(this.state.user)
    }

    refreshUsers(){
        UserDataService.retrieveUser(1)
        .then(
            response=> {
           this.setState({   
                   user:  new User(response.data.id,
                                    response.data.username,
                                    response.data.gender,
                                    response.data.date,
                                    response.data.phoneNumber,
                                    response.data.email)
               })
            }
        )

        console.log("refreshUsers")
        console.log(this.state.user)
    }

    render() {

        console.log("render")
        console.log(this.state.user)

        let userInfoForm =  <UserInfoForm User={this.state.user} title={this.state.title}/>

      return (

          <>    
            {userInfoForm}
          </>

      )

    }
}

export default UserInfoUpdate
