import React, {Component} from "react";
import { render } from "react-dom";
import InfiniteScroll from "react-infinite-scroll-component";
import {Image,Button,Container,Row,Col} from "react-bootstrap"

class ShowFriends extends Component {
  
  constructor(props){
    super(props)
        this.state = {
            items: Array.from({ length: 20 }),
            hasMore: true,
            valSearch: ""
        }
    }

    handleChange = (event) => {
    this.setState({
        [event.target.name] : event.target.value
    })
    }

  fetchMoreData = () => {
    if (this.state.items.length >= 500) {
      this.setState({ hasMore: false });
      return;
    }
    // a fake async api call like which sends
    // 20 more records in .5 secs
    setTimeout(() => {
      this.setState({
        items: this.state.items.concat(Array.from({ length: 20 }))
      });
    }, 500);
  };

  getFriends = () =>{

  }

  render() {
    return (
      <div>

         <Container className="show-friens-container">
                <Row>
                    <Col className="show-friens-col-2">
                          <input className="show-friens-col-1" type="text" name="valSearch" value={this.state.valSearch} placeholder="חיפוש" onChange={this.handleChange}></input>
                    </Col>
                    <Col className="show-friens-col-1">חברים</Col>
                </Row>
          </Container>
        
        <InfiniteScroll
          dataLength={this.state.items.length}
          next={this.fetchMoreData}
          hasMore={this.state.hasMore}
          loader={<h4>Loading...</h4>}
          height={500}
          endMessage={null}
        >

        <div className="show-friens-div-image">
        
        
        </div>

        </InfiniteScroll>
      </div>
    );
  }
}

export default ShowFriends
