import React ,{ Component } from "react";
import InfiniteScroll from "react-infinite-scroll-component";
import GridSystem from './GridSystem.js';
import {Image} from "react-bootstrap"



class ShowFriends extends Component {

  state = {
    
    hasMore: true,

    conection: 
    [  
       {next: null,  index: "1",  image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
       {next: "1",   index: "2",  image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
       {next: null,  index: "3",  image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
       {next: "2",   index: "6",  image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},  
       {next: "9",   index: "8",  image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
       {next: "3",   index: "9",  image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},         
       {next: "6",   index: "12", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
       {next: "8",   index: "13", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},             
       {next: "21",  index: "16", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
       {next: "16",  index: "17", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
       {next: "23",  index: "18", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},                   
       {next: "25",  index: "21", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},                   
       {next: "24",  index: "23", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
       {next: "29",  index: "24", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
       {next: null,  index: "25", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},                         
       {next: null,  index: "29", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'}
     ],

     conection2: 
     [  
        {next: null,  index: "1",  image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},
        {next: "1",   index: "2",  image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},
        {next: null,  index: "3",  image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},
        {next: "2",   index: "6",  image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},  
        {next: "9",   index: "8",  image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},
        {next: "3",   index: "9",  image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},         
        {next: "6",   index: "12", image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},
        {next: "8",   index: "13", image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},             
        {next: "21",  index: "16", image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},
        {next: "16",  index: "17", image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},
        {next: "23",  index: "18", image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},                   
        {next: "25",  index: "21", image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},                   
        {next: "24",  index: "23", image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},
        {next: "29",  index: "24", image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},
        {next: null,  index: "25", image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'},                         
        {next: null,  index: "29", image: 'https://i.pinimg.com/originals/d9/de/11/d9de112b2c4aedef6df31d05194adf21.jpg'}
      ],

        arr: [true,true,true,true,true,true,
              true,true,true,true,true,true,     
              true,true,true,true,true,true,
              true,true,true,true,true,true,
              true,true,true,true,true,true,
              ],

  };

  fetchMoreData = () => {
    if (this.state.conection.length >= 500) {
      this.setState({ hasMore: false });
      return;
    }

    setTimeout(() => {
        this.setState({
            conection: this.state.conection.concat(this.state.conection2)
        });
    }, 500);

    // a fake async api call like which sends
    // 20 more records in .5 secs
    // setTimeout(() => {
    //   this.setState({
    //     items: this.state.conection.concat(Array.from({ length: 20 }))
    //   });
    // }, 500);
  };

  render() {
    return (

      <div>

        <h1>demo: react-infinite-scroll-component</h1>
        <hr />

        <InfiniteScroll
          dataLength={this.state.conection.length}
          next={this.fetchMoreData}
          hasMore={this.state.hasMore}
          loader={null}
          height={400}
          endMessage={null}
        >

{/* 
         {/* <GridSystem colCount={5} rowcount={6} emptyOrfull={this.state.arr} >          
                    {this.state.conection.map(c => <Image src={c.image} roundedCircle className="grid-image" id={c.index} /> )}                                             
         </GridSystem> */} */}

        </InfiniteScroll>

      </div>

    );
  }
}

export default ShowFriends
