import axios from "axios"
import {API_URL} from "./Constants"

export const USER_NAME_ATTRIBUTE_NAME = "authenticatedUser"


class AuthenticationService{

    executeJwtAuthenticationService(username, password) {
        return axios.post(`${API_URL}/authenticate`, {
            username,
            password
        })
    }

    createJwtToken(token) {
        return 'Bearer ' + token
    }

    registerSuccessfulLoginForJwt(phonenumber){
        sessionStorage.setItem(USER_NAME_ATTRIBUTE_NAME,phonenumber);
    }

    logout(){
        sessionStorage.removeItem(USER_NAME_ATTRIBUTE_NAME);
    }

    isUserLoggedIn(){
        let user = sessionStorage.getItem(USER_NAME_ATTRIBUTE_NAME)
        
        if(user === null) return false

        return true

    }

    getLoggedInUserName(){

        let user = sessionStorage.getItem(USER_NAME_ATTRIBUTE_NAME);
        if(user===null) 
            return '';

         return user;

    }

    makeintecaptors(){

        instance.interceptors.request.use(function (config) {

            let basicAuthHeader = 'Bearer ' + "eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJpbjI4bWludXRlcyIsImV4cCI6MTYzOTIxNjYwNSwiaWF0IjoxNjM4NjExODA1fQ.gR46C3ZjCeASAXqWaWE6R0BDDD8n441EhoSpBpF07SnI6sEOxnOPhORZ0cUEolWtZaXdaIN5eT7wMJeaKYbaSA"
        
            config.headers.authorization = basicAuthHeader
        
             return config;
        
         }, function (error) {
             // Do something with request error
             return Promise.reject(error)
         })
         
    }

}

export default new AuthenticationService()
