import React, { Component } from 'react'
import UserDataService from "../api/user/UserDataService";
import {Button} from "react-bootstrap"

class Test extends Component {

    constructor(props){
        super(props)
            this.state = {
                message: "",
                inputValue: "",
                users: []
            }
    }


    componentDidMount(){
        UserDataService.retrieveAllUsers()
         .then(
             response=> {
            this.setState({   
                    users: response.data
                })
             }
         )

    }

    deleteUserClicked = (userId) =>{
        console.log(userId)
        UserDataService.deleteUser(userId)
            .then(
                response =>{
                    this.setState({
                        message: `Delete of user ${userId} successful `
                    })
                }
            )

    }

    updateInputValue = (props) =>{
        this.setState({
            inputValue : props.target.value
        });
    }

    render() {
        return (

            <>
            <input type="text" value={this.state.inputValue} onChange={this.updateInputValue} />
            <Button onClick={ () => this.deleteUserClicked(this.state.inputValue) }>מחק</Button>

            <div btn btn-wa>{this.state.message}</div>

            <ul>
                {this.state.users.map(u=><li>{u.id}</li>)}
            </ul>

            </>

        )
    }
}

export default Test
