import React, { Component } from 'react'
import ViewerNetworkVisualization from "./components/Viewer_network_visualization/ViewerNetworkVisualization.js"
import ViewerPotenMatch from "./components/viewer_potenMatch/ViewerPotenMatch.js"
import {UserDataService} from '@api/index.js'
import AuthenticationService from '@services/AuthenticationService.js'

 class SearchMatch extends Component {
 
    constructor(props){
         super(props)
            this.state = {
                   id: AuthenticationService.getLoggedInUserId(),
                   optionMatches: null,
                   indexCurrentMatch: 0,
            }
    }

    componentDidMount(){
         this.refresh()
    }

    refresh = async () =>{

        const res = await UserDataService.getOptionMatches(this.state.id); 

        const optionMatches = this.buildOptionMatches(res.data);

        this.setState(
            { optionMatches: optionMatches}
            )
    }

    likeClicked = () =>{
        
        if(this.state.indexCurrentMatch < this.state.optionMatches.length -1 )
            this.setState(prevstate => (
                { indexCurrentMatch: prevstate.indexCurrentMatch + 1}
            ))
        else{
            this.setState(prevstate => (
                { indexCurrentMatch: 0}
            ))
        }    
    }

    backClicked = () =>{

        if(this.state.indexCurrentMatch == 0 )
            this.setState(prevstate => (
                { indexCurrentMatch: this.state.optionMatches.length -1}
            ))
        else{
            this.setState(prevstate => (
                { indexCurrentMatch: prevstate.indexCurrentMatch - 1}
            
            ))
        }
    }

    unlikeClicked = () =>{

        if(this.state.indexCurrentMatch < this.state.optionMatches.length -1 )
            this.setState(prevstate => (
                { indexCurrentMatch: prevstate.indexCurrentMatch + 1}
            ))
        else{
            this.setState(prevstate => (
                { indexCurrentMatch: 0}
            ))
        }  
    }  

    buildOptionMatches(pathes){

        const OptionMatches = new Set()

        for(let i = 0 ; i <=pathes.length-1; i++ ){
            OptionMatches.add(JSON.stringify(pathes[i][0]))
        }

        return Array.from(OptionMatches);

        // var list = [JSON.stringify({id: 123, 'name': 'ABC'}), JSON.stringify({id: 123, 'name': 'ABC'})]; 
        // var unique_list = new Set(list); // returns Set {"{'id': 123, 'name': 'ABC'}"}
        // var list = Array.from(unique_list); // converts back to an array, and you can unstringify the results accordingly.

    }
    
    render() {

        let optionMatch;

        if( this.state.optionMatches != null )
             optionMatch = this.state.optionMatches[this.state.indexCurrentMatch][0];
        else
             optionMatch = null;

        return (
            <div>

               <ViewerNetworkVisualization/> 

               <ViewerPotenMatch likeClicked = {this.likeClicked} 
                                 backClicked = {this.backClicked}
                                 unlikeClicked = {this.unlikeClicked}
                                 optionMatch = {optionMatch} /> 
        
            </div>
        )
    }
}

export default SearchMatch
