import React from 'react'
import {Container, Row, Col,Image} from "react-bootstrap"

const GridSystem = ({colCount,rowcount, children, emptyOrfull}) => {
    let ichildren =0
    let iGridSystem =0

    const buildGrid = ()=> {

        return (renderRows())
    
    }

    const renderRows = () =>{
        let rows = []

        for(let row = 0;row<rowcount;row++){

            rows.push(
                <Row className="Row">
                    {
                        renderCols()
                    }
               </Row>
            )
        }

        return rows;

    }                               

    const renderCols = () =>{

        let cols = []

        for(let col=0; col<colCount;col++){

            if(emptyOrfull[iGridSystem]){
                cols.push(
                    <Col className="Col text-center">  
                    {children[ichildren]}       
                    </Col>
                )
                ichildren++;
            }else{
                cols.push(
                    <Col className="Col text-center">
                    </Col>
                )
            }
            iGridSystem++
        }
       return cols
    }

    return (

        <Container className="container w-50 p-3 position-absolute container-grid-syatem">
            {
            buildGrid()
            }
        </Container>

    );
};

export default GridSystem;
