import React, { Component } from 'react'
import UserInfoForm from './UserInfoForm'
import moment from 'moment'
import {User} from "./User"
import UserDataService from "../api/user/UserDataService";


class UserInfoUpdate extends Component {

    constructor(props){
        super(props)
            this.state = {
               user: new User()
            }
    }

    componentDidMount(){
        this.refreshUsers();
    }
    
    refreshUsers(){
        UserDataService.retrieveUser(1)
        .then(response =>{
             this.setState(
                         { user: response.data})
                        //    ({ user: prevState.user.copy(response.data)}))
             });      
    }

    render() {

    const title = "עדכון פרטים"

      return (

          <>    
             <UserInfoForm user={this.state.user} title={title}/>
          </>

      )

    }
}

export default UserInfoUpdate
