import React, {Component} from "react";
import UserDataService from "@api/user/UserDataService";
import GridSystem from '@components/GridSystem.js';

class ShowFriendRequests extends Component {
  
  constructor(props){
    super(props)
        this.state = {
            listRequests: null,
            filteredListRequests: null,
        }
  }

  componentDidMount(){
    this.refresh()
  }

  refresh = async () =>{

    var requestParam = {
        params: {
          type: "F",
          status: "S"
        }
    }

    const listRequests = await UserDataService.getRelationships(this.state.id,requestParam);

    this.setState({listFriendRequests: listFriendRequests.data,
                   filteredListFriendRequests: listFriendRequests.data
                  })

  }

  buildListViewFriends = () =>{

    const listViewFriendRequests = [];
    let view

    for(let i = 0; this.state.filteredListFriendRequests != null && i<this.state.filteredListFriendRequests.length;i++){

       const view = this.buildViewFriend(this.state.filteredFriendsList[i].username,
                                        this.state.filteredFriendsList[i].id,
                                        this.state.filteredFriendsList[i].image[0].content,
                                        this.cancelFriendshipClick)

            istViewFriendRequests.push( view )
        }

      return ListViewFriends
    }
  
  getNumRow = (colCount) =>{

    let rowcount

    if(this.state.filteredListFriendRequests!=null){
      rowcount = Math.ceil(this.state.filteredListFriendRequests / colCount) ;
     }else{
       rowcount = 0;
     }

     return rowcount

  }

  render() {

    const colCount = 2
    const rowcount = this.getNumRow(colCount)
    const ListViewFriends = this.buildListViewFriends()

    return (

      <div className="mx-auto">

                {/* <SubNavbar handleChange={this.handleChange} valSerach={this.state.valSerach}/> */}

                <GridSystem rowcount = {rowcount} 
                            colCount = {colCount} 
                            view={ListViewFriends}
                />
            
      </div>

    );
  }
}

export default ShowFriendRequests
