const instance = axios.create({
    
    baseURL: `JPA_API_UR`,
    timeout: 10000,
    params: {} // do not remove this, its added to add params later in the config
    
});

instance.interceptors.request.use(function (config) {

    let basicAuthHeader = 'Bearer ' + "eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJpbjI4bWludXRlcyIsImV4cCI6MTYzODQ1MTk3NSwiaWF0IjoxNjM3ODQ3MTc1fQ.qU_Yf-zqlcCkkgxR8Bsvx1OMpQmGTlxablg_4DZqsOUQJmvE5X2V0xfF-OUtoeU66I8bgZqt2Hdq_FGVsEIEnw"

    config.headers.authorization = basicAuthHeader

     return config;

 }, function (error) {
     // Do something with request error
     return Promise.reject(error)
 })

