import React, { Component } from 'react'
import {Image, Button} from "react-bootstrap"
import SubNavbar from "@components/SubNavbar.js";
import UserDataService from "@api/user/UserDataService";

class FindFriends extends Component {

  constructor(props){
    super(props)
        this.state = {
           id: AuthenticationService.getLoggedInUserId(),
           search: '',
           searchResult: null,
           showResult: false,
           imageResult: '',
           idResult: '',
           usernameResult: '',
           isRequestSent: false,
           isAlreadyFriend: false
        }
  }

  handleChange = async (event) => {
    var requestParam = {
      params: {
          userId: Number(this.state.id) ,
          otherId: friendId,
          type: 'FRIEND' ,
          status: 'ACTIVE' 
      }
    }
    const relationShipt = await UserDataService.getRelationship(id ,event.target.value, )

    try{
    const searchResult = await UserDataService.retrieveUser(event.target.value)
 
      this.setState({
          [event.target.name] : event.target.value,
          searchResult : searchResult.data,
          showResult: true,
          imageResult: searchResult.data.image[0].content,
          idResult: searchResult.data.id,
          usernameResult: searchResult.data.username
      })
  
  }
  catch{
      this.setState({
        [event.target.name] : event.target.value,
        showResult: false
      })
  }

  }

  AddFriend = async (friendId) =>{

    var requestParam = {
      params: {
          userId: Number(this.state.id) ,
          otherId: friendId,
          type: 'FRIEND' ,
          status: 'ACTIVE' 
      }
    }

    this.setState(
                    {isRequestSent: true}
                 )

  //await UserDataService.updateRelationship(this.state.id,requestParam);
   
  }

  isAlreadyFriend = () =>{
                           
  }

  cancelRequest = () =>{
                            
  }

    render() {

        const title = "Find new friends"
        const btnText = "My friends"

        return (

            <div className="mx-auto">

                        <SubNavbar handleChange={this.handleChange} 
                                   valSerach={this.state.valSerach}
                                   btnText={btnText}
                                   title={title}/>

                        {this.state.showResult && <div className='w-50 mx-auto'>

                                                 <Image src={`data:image/jpeg;base64,${this.state.imageResult}`} 
                                                        className=" size-image-find-Friends mt-3 mb-3 d-block mx-auto" />  
   
                                                 <div className=' w-25 mx-auto'>{this.state.usernameResult}</div>
   
                                                 <div className=' w-25  mx-auto'>
                                                      
                                                       { (this.state.isRequestSent == false) && <Button onClick={e => this.AddFriend(e.target.id)}  
                                                                                             id={this.state.idResult} 
                                                                                             className=' w-100 c1 mt-4  mx-auto'>Add Friend</Button>}

                                                       {this.state.isRequestSent && <p class="text-muted mx-auto mt-4">Request sent</p>}

                                                       { this.state.isRequestSent && <Button onClick={e => this.cancelRequest(e.target.id)}  
                                                                                             id={this.state.idResult} 
                                                                                             className='btn btn-secondary w-100 c1 mx-auto'>Cancel request</Button>}
                                                 </div>

                                            </div> }

            </div>

        )
    }
}

export default FindFriends
