import React, {Component} from "react";
import { render } from "react-dom";
import InfiniteScroll from "react-infinite-scroll-component";
import {Image,Button} from "react-bootstrap"

class ShowFriends extends Component {
  
  constructor(props){
    super(props)
        this.state = {
            items: Array.from({ length: 20 }),
            hasMore: true,
            valSearch: ""
        }
    }

    handleChange = (event) => {
    this.setState({
        [event.target.name] : event.target.value
    })
    }

  fetchMoreData = () => {
    if (this.state.items.length >= 500) {
      this.setState({ hasMore: false });
      return;
    }
    // a fake async api call like which sends
    // 20 more records in .5 secs
    setTimeout(() => {
      this.setState({
        items: this.state.items.concat(Array.from({ length: 20 }))
      });
    }, 500);
  };

  render() {
    return (
      <div>

        <div className="show-friens-header"> 

        <div className="show-friens-input-1 d-inline">
            <input type="text" name="valSearch" value={this.state.valSearch} placeholder="חיפוש" onChange={this.handleChange}></input>
        </div>

            <div> className="show-friens-input-2">חברים </div>

        </div>

        <InfiniteScroll
          dataLength={this.state.items.length}
          next={this.fetchMoreData}
          hasMore={this.state.hasMore}
          loader={<h4>Loading...</h4>}
          height={500}
          endMessage={null}
        >

        <div className="show-friens-header"> 
        
         </div>

        <div className="show-friens-div-image">
          {this.state.items.map((i, index) => (
              <div className="bdika">
                    <Button className="show-friens-btn">שלח הודעה</Button>
                    <span className="">יונתן לוי</span>
                    <Image src="https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg"  className="show-friens-image"/>
              </div>
          ))}
        </div>

        </InfiniteScroll>
      </div>
    );
  }
}

export default ShowFriends
