import {ToggleButton,radioValue} from "react-bootstrap"

export const ButtonGroup = (props) => {

    return <ButtonGroup>

                        {props.radios.map((radio, idx) => (
                          <ToggleButton
                            key={idx}
                            id={`radio-${idx}`}
                            type="radio"
                            variant={idx % 2 ? 'outline-success' : 'outline-danger'}
                            name="radio"
                            value={radio.value}
                            checked={radioValue === radio.value}
                            onChange={props.handleChange}
                          >
                            {radio.name}
                          </ToggleButton>
                        ))}

                </ButtonGroup>
 }
