import React, { Component } from 'react'

import {FloatingLabelsDate, FloatingLabelsEmail, FloatingLabelsPassword, FloatingLabelsPhone,
         FloatingLabelsUsername, SelectsGender, TextAreasDescription} from '@components/forms/index.js'

import {Container, Row, Col,Form   } from 'react-bootstrap'

import moment from 'moment'

import UserDataService from '@api/user/UserDataService.js'

import AuthenticationService from '@services/AuthenticationService.js'

import {User} from '@components/User.js'

class RegistrationForm extends Component {

    constructor(props){
        super(props)

            this.state = {

                            id: 0, 
                            username:   '',
                            gender: '', 
                            date:   moment(new Date()).format('YYYY-MM-DD'), 
                            phone:  '', 
                            email:  '', 
                            password:   '',
                            confirm: '',
                            passwordHash: ''

            }
    }

    // shouldComponentUpdate(nextState) { 
    //     if (nextState.passwordHash ===  '') { 
    //       return true;
    //     }
    //     return false;
    // }

    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
    })
    }

    handleSubmit = (event)=>{
        this.createAccount()
        event.preventDefault();
    }

    createAccount = () =>{

        this.setPasswordHash();
        const user = this.getUser();
        UserDataService.createUser(user)
        .then(() => this.props.history.push('/search/match/'))
    
    }

    setPasswordHash = () =>{
        const username = this.state.username;
        const password = this.state.password;
        const passwordHash = AuthenticationService.executeJwtAuthenticationService(username,password);
        this.setState({passwordHash: passwordHash });
    }

    getUser = () =>{
        let user = new User()
        return user.copy(this.state)
    }

    render() {

        const btnTxt = "צור חשבון"
       
        return (

           <>

                <Container>
                <Form onSubmit={this.handleSubmit} noValidate>
                   <Row className="justify-content-md-center">

                        <Col lg="4">
                            <FloatingLabelsPhone handleChange={this.handleChange} val={this.state.phone}/>
                        </Col>

                        <Col lg="4">
                            <FloatingLabelsEmail handleChange={this.handleChange} val={this.state.email}/>
                        </Col>

                   </Row>

                   <Row className="justify-content-md-center">

                        <Col    lg="8">

                            <FloatingLabelsUsername handleChange={this.handleChange} val={this.state.username}/>  

                        </Col>

                   </Row>

                    <Row className="justify-content-md-center">

                        <Col lg="4">
                            <FloatingLabelsPassword handleChange={this.handleChange} val={this.state.password} name="password"/>
                        </Col>

                        <Col lg="4">
                            <FloatingLabelsPassword handleChange={this.handleChange} val={this.state.confirm} name="confirm"/>
                        </Col>

                   </Row>

                    <Row className="justify-content-md-center">

                        <Col lg="4">
                            <FloatingLabelsDate handleChange={this.handleChange} val={this.state.date}/>
                        </Col>

                        <Col lg="4">
                            <SelectsGender/>
                        </Col>

                    </Row>

                    <Row className="justify-content-md-center">

                        <Col    lg="8">

                            < TextAreasDescription/>  

                        </Col>

                   </Row>

                   <button type="submit" value="submit" > {btnTxt}  </button>
                   </Form>
                 </Container> 
                 
           </>

        )
    }
}

export default RegistrationForm
