import React from 'react'
import { Container , Row, Col} from "react-bootstrap"
import viewFriends from './component/viewFriends.js'

const GridSystem = (props) => {

    let indexFriends =0
    
    const buildGrid = ()=> {

        return (renderRows())
    
    }

    const renderRows = () =>{
        let rows = []

        for( let row = 0; row < props.rowcount ; row++ ){

            rows.push(
                <Row className="Row">
                    {
                        renderCols()
                    }
               </Row>
            )
        }

        return rows;

    }                               

    const renderCols = () =>{

        let cols = []

        for( let col=0; col < props.colCount && indexFriends < props.friends.length ; col++ ){

            if(props.friends!=null){
                cols.push(
                    <Col className="Col text-center">  
                        
                                <viewFriends username={ props.friends[indexFriends].username}
                                             image={ props.friends[indexFriends].image[0].content}
                                             id = {props.friends[indexFriends++].id}  />
                              
                    </Col>
                )
            }
        }

        return cols
}

    return (

        <Container className="container w-50 p-3">
            {
            buildGrid()
            }
        </Container>

    );
};

export default GridSystem;
