class LoginComponent extends Component{

    constructor(props){
        super(props)
            this.state = {
                areacode: "+972",
                phonenumber: "",
                password: "",
                isValidPhoneNumber: true,
                isvalidPassword: true,
                stage: "Enter phone number"
            }
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
        })
    }

    continuePhoneNumberClicked = () => {

            if(isValidPhoneNumber(this.state.areacode + this.state.phonenumber )){
                this.setState({  
                    stage: "Enter password"
                }) 
            }else{
                this.setState({  
                    isValidPhoneNumber: false
                }) 
            }
    }

    continuePasswordClicked = () => {

        if(this.state.password === "1234"){
            this.props.history.push(`/welcome/${this.state.phonenumber}`)
        }else{
            this.setState({  
                isvalidPassword: false
            }) 
        }
    }   

    render(){
        return (

            <>
                {this.state.stage ==="Enter phone number" &&   
                                                             <div>
                                                                  <h1>המספר שלי הוא</h1>
                                                                  {! this.state.isValidPhoneNumber && <div>יש להזין מספר טלפון חוקי</div>}
                                                                  <input type="text" name="areacode" value={this.state.areacode} onChange={this.handleChange}></input>
                                                                  <input type="text" name="phonenumber" value={this.state.phonenumber} onChange={this.handleChange}></input>
                                                                  <button onClick={this.continuePhoneNumberClicked}>המשך</button>
                                                             </div>}

                {this.state.stage ==="Enter password" &&   
                                                        <div>
                                                             <h1>הקוד שלי הוא</h1>
                                                             {! this.state.isvalidPassword && <div>הקוד שהזנת לא חוקי. יש לנסות שוב</div>}
                                                             <input type="password" name="password" onChange={this.handleChange}></input>
                                                             <button onClick={this.continuePasswordClicked}>המשך</button>
                                                        </div>}                                
            </>

        )
        
    }

}


