import React, { Component } from 'react'
import ViewerNetworkVisualization from "./components/Viewer_network_visualization/ViewerNetworkVisualization.jsx"
import ViewerPotenMatch from "./components/viewer_potenMatch/ViewerPotenMatch.js"
import {UserDataService} from '@api/index.js'
import AuthenticationService from '@services/AuthenticationService.js'

class SearchMatch extends Component {
 
    constructor(props){
         super(props)
            this.state = {
                   id: AuthenticationService.getLoggedInUserId(),
                   optionMatches: null,
                   indexCurrentMatch: 0,
            }
    }

    componentDidMount(){
         this.refresh()
    }

    refresh = async () =>{

        const res = await UserDataService.getOptionMatches(this.state.id); 

        this.setState(prevstate => (
            { optionMatches: res.data }
            ))
    }

    likeClicked = () =>{
        
        if(this.state.indexCurrentMatch < this.state.optionMatches.length -1 )
            this.setState(prevstate => (
                { indexCurrentMatch: prevstate.indexCurrentMatch + 1}
            ))
        else{
            this.setState(prevstate => (
                { indexCurrentMatch: 0}
            ))
        }    
    }

    backClicked = () =>{
        this.setState(prevstate => (
            { indexCurrentMatch: prevstate.indexCurrentMatch - 1}
            ))
    }

    unlikeClicked = () =>{
        this.setState(prevstate => (
            { indexCurrentMatch: prevstate.indexCurrentMatch + 1}
            ))
    }

    IncreaseOrDecrease

    render() {

        let optionMatch;

        if( this.state.optionMatches != null )
             optionMatch = this.state.optionMatches[this.state.indexCurrentMatch][0];
        else
             optionMatch = null;

        return (
            <div>

               <ViewerNetworkVisualization/> 

               <ViewerPotenMatch likeClicked = {this.likeClicked} 
                                 backClicked = {this.backClicked}
                                 unlikeClicked = {this.unlikeClicked}
                                 optionMatch = {optionMatch} /> 
        
            </div>
        )
    }
}

export default SearchMatch
