import React from 'react'
import {Container, Row, Col} from "react-bootstrap"

const GridSystem = ({colCount, children}) => {
    let rowcount = Math.floor(children.length / colCount) + 1
    let index =0

    const buildGrid = ()=> {

        return (renderRows())
    
    }

    const renderRows = () =>{
        let rows = []

        for(let row = 0;row<rowcount;row++){

            rows.push(
                <Row className="Row">
                    {
                        renderCols()
                    }
               </Row>
            )
        }

        return rows;

    }                               

    const renderCols = () =>{

        let cols = []

        for(let col=0; col<colCount;col++){
            if(index<children.length){
                if(children[index].flag)
                    cols.push(
                        <Col className="Col text-center">
                            {children[index]}
                    </Col>
                )
            }
            index++
        }
       return cols
    }

    return (

        // <Container className="container w-50 p-3 position-absolute sticky-top my1">
        <Container className="container w-50 p-3 position-absolute container-grid-syatem">
            {
            buildGrid()
            }
        </Container>

    );
};

export default GridSystem;
