import React, { Component } from 'react'
import {Image, Button} from "react-bootstrap"
import SubNavbar from "@components/SubNavbar.js";
import UserDataService from "@api/user/UserDataService";

class FindFriends extends Component {

  constructor(props){
    super(props)
        this.state = {
           search: '',
           searchResult: null,
           show: false,
           imageResult: '',
           idResult: '',
           username: ''
        }
  }

  handleChange = (event) => {
    
    const searchResult = this.getSearchResult(event.target.value)
    if(searchResult.data != undefined){
      this.setState({
          [event.target.name] : event.target.value,
          searchResult : searchResult.data,
          show: true
      })
    }
    else{
      this.setState({
        [event.target.name] : event.target.value,
        show: false
      })
    }
    
  }

  getSearchResult = async (valSearch) =>{

    const res =  await UserDataService.retrieveUser(valSearch)

    return res

  }

  AddFriend = async (friendId) =>{

    var requestParam = {

      params: {
          userId: Number(this.state.id) ,
          otherId: friendId,
          type: 'FRIEND' ,
          status: 'ACTIVE' 
      }

  }
  
  await UserDataService.updateRelationship(this.state.id,requestParam);

  }

    render() {

        const title = "Find friends"
        const btnText = "My friends"

        return (

            <div className="mx-auto">

                        <SubNavbar handleChange={this.handleChange} 
                                   valSerach={this.state.valSerach}
                                   btnText={btnText}
                                   title={title}/>

                        {this.state.show && <div className='wrapper'>
                                              <Image src={`data:image/jpeg;base64,${image}`} 
                                                     className="grid-image mt-3 c2" />  
                                              <Button onClick={e => AddFriend(e.target.id)}  id={id} className='c1 mt-4'>Add Friend</Button>
                                            <div className='c3 d-inline-block'>{username}</div> 

            </div> }

            </div>

        )
    }
}

export default FindFriends
