import React, {Component} from "react"
import AuthenticationService from "@services/AuthenticationService.js"
import { FloatingLabelsPassword, FloatingLabelsPhone } from '@components/forms/floating_labels/index.js'



class Login extends Component{

    constructor(props){
        super(props)
            this.state = {
                areacode: "+972",
                phone: "",
                isValidPhoneNumber: true,
                password: "",
                isvalidPassword: true,
            }
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
        })
    }

    continueClicked = async (event) => {

            //if(AuthenticationService.executeAuthenticationService(this.state.phonenumber, this.state.password)){
                AuthenticationService.registerSuccessfulLogin(this.state.phonenumber)
                this.props.history.push('/search/match/')
            //}
            
            this.setState({  isvalidPassword: false })

        }



    render(){
        return (

            <>
            
    

                {/* <div>
                    <Card className="mx-auto card-home-page">
                        <Card.Body>
                            <Card.Title>המספר שלי הוא</Card.Title>
                                {! this.state.isValidPhoneNumber && 
                                   <Alert variant="warning"> יש להזין מספר טלפון חוקי </Alert>}
                                   <input type="text" name="areacode" value={this.state.areacode} onChange={this.handleChange}></input>
                                   <input type="text" name="phonenumber" value={this.state.phonenumber} onChange={this.handleChange}></input>
                                   <button onClick={this.continueClicked}>המשך</button>
                            </Card.Body>
                    </Card>
                </div>  */}

                <FloatingLabelsPassword handleChange={this.handleChange} val={this.state.password} name="password"/>
                <FloatingLabelsPhone handleChange={this.handleChange} val={this.state.phone} name="phone"/>


            </>

        )
        
    }

}

export default Login;
