import React, {Component} from "react"


class MyApp extends Component {
    render(){

        return(
            <div className="MyApp">
                <LoginComponent/>
            </div>
        )
    }
}

class LoginComponent extends Component{

    constructor(props){
        super(props)
            this.state = {
                areacode: "+972",
                phonenumber: "",
                stage: "1"
            }
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
        })
    }


    ContinueClicked = () => {
     
    }

    render(){
        return (

            <>
                {this.state.stage ==="1" &&   <div>
                                                  <h1>המספר שלי הוא</h1>
                                                  <input type="text" name="areacode" value={this.state.areacode} onChange={this.handleChange}></input>
                                                  <input type="text" name="phonenumber" value={this.state.phonenumber} onChange={this.handleChange}></input>
                                                  <button onClick={this.ContinueClicked}>המשך</button>
                                               </div>}

                {this.state.stage ==="2" &&   <div>
                                                  <h1>הקוד שלי הוא</h1>
                                                  <input type="text" name="password" value={this.state.areacode} onChange={this.handleChange}></input>
                                                  <button onClick={this.ContinueClicked}>המשך</button>
                                               </div>}
                                               
              
            </>

        )
        
    }

}


export default MyApp
