//import axios from "axios"

import instance from "../../components/AxiosService"
import {API_URL,JPA_API_URL } from "../../components/Constants.js"

// const axios = getAxios("in28minutes","dummy");

class UserDataService{

    retrieveAllUsers(){
     
        return instance.get(`${JPA_API_URL}/users`)

    }

    retrieveUser(userId){

        return instance.get(`${JPA_API_URL}/users/${userId}`)

    }

    deleteUser(id) {
        return instance.delete(`${JPA_API_URL}/users/${id}`);
    }

    updateUser(user, userId){

        return instance.put(`${JPA_API_URL}/users/${userId}`, user)

    }

    createUser(user){

        return instance.post(`${JPA_API_URL}/users`, user)

    }

}

export default new UserDataService()
