import React, { Component } from 'react'
import {} from "react-bootstrap"
import SubNavbar from "@components/SubNavbar.js";
import UserDataService from "@api/user/UserDataService";

class FindFriends extends Component {

  constructor(props){
    super(props)
        this.state = {

           search: '',
           searchResult: null
        }
  }

  handleChange = (event) => {

    const searchResult = this.getSearchResult(event.target.value)
    
    this.setState({
        [event.target.name] : event.target.value,
        searchResult : searchResult
    })

  }

  getSearchResults = async (valSearch) =>{

    res =  await UserDataService.retrieveUser(valSearch)

    return res.data

  }

  AddFriend = () =>{
    
  }

    buildViewSearchResult = (username,id,image,AddFriend) => {

      return    <div className='wrapper'>
                <Image id={id+"image"}
                onMouseEnter={e => this.viewMoreActions(e.target.id)} 
                onMouseLeave={ e  =>  this.cancelViewMoreActions(e.target.id)}
                src={`data:image/jpeg;base64,${image}`} 
                className="grid-image mt-3 c2" />  
                <Button onClick={e => AddFriend(e.target.id)}  id={id} className='c1 mt-4'>Add Friend</Button>
                <div className='c3 d-inline-block'>{username}</div> 
              </div> 
    }


    render() {

        const title = "Find friends"
        const btnText = "My friends"

        return (

            <div className="mx-auto">

                        <SubNavbar handleChange={this.handleChange} 
                                   valSerach={this.state.valSerach}
                                   btnText={btnText}
                                   title={title}/>

            </div>

        )
    }
}

export default FindFriends
