            { headers: { authorization: this.createBasicAuthToken(phonenumber, password) } })
    }

    executeJwtAuthenticationService(username, password) {
        return axios.post(`${API_URL}/authenticate`, {
            username,
            password
        })
    }

    createBasicAuthToken(username, password) {
        return 'Basic ' + window.btoa(username + ":" + password)
    }

    registerSuccessfulLogin(phonenumber,password){

        sessionStorage.setItem('authenticatedUser',phonenumber);

    }

